					$this->setNew(false);
				} else {
					$affectedRows += ExpositorPeer::doUpdate($this, $con);
				}

				$this->resetModified(); // [HL] After being saved an object is no longer 'modified'
			}

			if ($this->collExpositorFerias !== null) {
				foreach ($this->collExpositorFerias as $referrerFK) {
					if (!$referrerFK->isDeleted()) {
						$affectedRows += $referrerFK->save($con);
					}
				}
			}

			if ($this->collExpositorLineaeditorials !== null) {
				foreach ($this->collExpositorLineaeditorials as $referrerFK) {
					if (!$referrerFK->isDeleted()) {
						$affectedRows += $referrerFK->save($con);
					}
				}
			}

			if ($this->collPagoExpositors !== null) {
