	 *
	 * @param      boolean $deep Whether to also clear the references on all referrer objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collExpositorFerias) {
				foreach ($this->collExpositorFerias as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collExpositorLineaeditorials) {
				foreach ($this->collExpositorLineaeditorials as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collPagoExpositors) {
				foreach ($this->collPagoExpositors as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collStatuss) {
				foreach ($this->collStatuss as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		if ($this->collExpositorFerias instanceof PropelCollection) {
			$this->collExpositorFerias->clearIterator();
		}
		$this->collExpositorFerias = null;
		if ($this->collExpositorLineaeditorials instanceof PropelCollection) {
			$this->collExpositorLineaeditorials->clearIterator();
		}
		$this->collExpositorLineaeditorials = null;
		if ($this->collPagoExpositors instanceof PropelCollection) {
			$this->collPagoExpositors->clearIterator();
		}
		$this->collPagoExpositors = null;
		if ($this->collStatuss instanceof PropelCollection) {
			$this->collStatuss->clearIterator();
		}
		$this->collStatuss = null;
		$this->aPais = null;
		$this->aUsuario = null;
	}

