			if ($ret) {
				FeriaQuery::create()
					->filterByPrimaryKey($this->getPrimaryKey())
					->delete($con);
				$this->postDelete($con);
				// symfony_behaviors behavior
				foreach (sfMixer::getCallables('BaseFeria:delete:post') as $callable)
				{
				  call_user_func($callable, $this, $con);
				}

