	/**
	 * Resets all references to other model objects or collections of model objects.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect
	 * objects with circular references (even in PHP 5.3). This is currently necessary
	 * when using Propel in certain daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all referrer objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collEstados) {
				foreach ($this->collEstados as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collExpositors) {
				foreach ($this->collExpositors as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collExpositorFerias) {
				foreach ($this->collExpositorFerias as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collFerias) {
				foreach ($this->collFerias as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collPonentes) {
				foreach ($this->collPonentes as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collSelloEditorials) {
				foreach ($this->collSelloEditorials as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		if ($this->collEstados instanceof PropelCollection) {
			$this->collEstados->clearIterator();
		}
		$this->collEstados = null;
		if ($this->collExpositors instanceof PropelCollection) {
			$this->collExpositors->clearIterator();
		}
		$this->collExpositors = null;
		if ($this->collExpositorFerias instanceof PropelCollection) {
			$this->collExpositorFerias->clearIterator();
		}
		$this->collExpositorFerias = null;
		if ($this->collFerias instanceof PropelCollection) {
			$this->collFerias->clearIterator();
		}
		$this->collFerias = null;
		if ($this->collPonentes instanceof PropelCollection) {
			$this->collPonentes->clearIterator();
		}
		$this->collPonentes = null;
		if ($this->collSelloEditorials instanceof PropelCollection) {
			$this->collSelloEditorials->clearIterator();
		}
		$this->collSelloEditorials = null;
	}

